package grotechjava;

public class Nested_If_Else {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 int age=19;
		 double salary=10000;
		if(age>18)
		{
			if(salary>100000)
			   System.out.println("1");
			else
				System.out.println("2");
		}
		
		else
			System.out.println("3");

	}

}
