//Print even no. from 0 to 100 
package grotechjava;

public class ForLoop_5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	
		/*
		 * for(int i=0;i<=100;i=i+2) { System.out.println(i); }
		 */

		for(int i=2;i<=100;i++)
		{
			System.out.println(i);
			i++;
		}
	}

}
