package grotechjava;

public class Math_Concept_2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//max()-->give max value
		System.out.println(Math.max(35.12, 35.123));
		System.out.println(Math.max(23, 55));
		
		
		//min()-->give min value
		System.out.println(Math.min(25, 55));
		System.out.println(Math.min(35.12, 35.123));
		
	}

}
